  #{emailStatus,jdbcType=BIT}, #{avatarBig,jdbcType=VARCHAR}, #{avatarHd,jdbcType=VARCHAR},
  #{avatarLocal,jdbcType=VARCHAR}, #{avatarMid,jdbcType=VARCHAR}, #{avatarSmall,jdbcType=VARCHAR},
  #{mobileStatus,jdbcType=BIT}, #{qq,jdbcType=VARCHAR}, #{sex,jdbcType=CHAR}, #{uid,jdbcType=VARCHAR}
  )
